const API_BASE_URL='https://685c5401769de2bf085c6ec6.mockapi.io/api/v1'; // ¡URL base corregida para incluir /api/v1!
const API_ENDPOINT='/departments';const departmentGrid=document.querySelector('.department-grid');const searchInput=document.getElementById('search-input');const searchButton=document.getElementById('search-button');const clearSearchButton=document.getElementById('clear-search-button');const publishButton=document.getElementById('publish-button');const minBedroomsSelect=document.getElementById('min-bedrooms');const minPriceInput=document.getElementById('min-price');const maxPriceInput=document.getElementById('max-price');const sortBySelect=document.getElementById('sort-by');const modalPublish=document.getElementById('modal-publish');const modalDetails=document.getElementById('modal-details');const closeButtons=document.querySelectorAll('.close-button');const publishForm=document.getElementById('publish-form');const modalPublishTitle=document.getElementById('modal-publish-title');const imageUpload=document.getElementById('image_upload');const imagePreview=document.getElementById('image-preview');const imageUrlUnchanged=document.getElementById('image_url_unchanged');const contactToggleButton=document.querySelector('.contact-toggle-button');const contactFormContainer=document.getElementById('contact-form-container');const contactForm=document.getElementById('contact-form');
const paginationControls=document.getElementById('pagination-controls');
const userActionButton = document.getElementById('user-action-button');
const modalAuth = document.getElementById('modal-auth');
const authForm = document.getElementById('auth-form');
